// Visit Agoda homepage
cy.visit(Cypress.env("agodaBaseUrl"));
// Verify homepage is loaded
_homePage.homePage.getWelcomeMessage().should("eq", "See the world for less");
// Navigate to Flight tab
_homePage.homePage.clickFlightTab();
// Close cookie consent popup if it appears
_homePage.homePage.clickCloseCookieButton();
// Fill in flight search details (pakai flightData)
_flightsTabPage.flightsTabPage.searchFlyingFrom(flightData.departureFrom);
_flightsTabPage.flightsTabPage.searchFlyingTo(flightData.arrivalTo);
const tomorrowDate = _flightsTabPage.flightsTabPage.getTomorrowDate();
_flightsTabPage.flightsTabPage.setDepartureDate(tomorrowDate);
_flightsTabPage.flightsTabPage.getDepartureDateDescription().then(text => {
  departureDate = text.trim();
  departureDate = departureDate.replace(/(\d{1,2})\s(\w{3})\s(\d{4})/, '$2 $1, $3');
});
const departureDateDesc = _flightsTabPage.flightsTabPage.getDepartureDateDescription();
_flightsTabPage.flightsTabPage.setOccupancy();
_flightsTabPage.flightsTabPage.clickSearchFlightsButton();
// Verify flight search results page is loaded
_flightSearchListPage.flightSearchListPage.getFlightSearchListTitle().should("have.text", "Departure to " + flightData.arrivalTo);
// Sort flights by departure time (early to late)
_flightSearchListPage.flightSearchListPage.sortByDepartureEarlyToLate();
// Get all data from the first flight option
_flightSearchListPage.flightSearchListPage.getFirstFlightOption().then(text => {
  flightName = text.trim();
  cy.log("Flight Name: " + flightName);
});
_flightSearchListPage.flightSearchListPage.getFirstFlightDepartureTime().then($departureTime => {
  departureTime = $departureTime;
  departureTime24h = _flightSearchListPage.flightSearchListPage.convertTo24Hour(departureTime);
  cy.log("Departure Time: " + departureTime);
});
_flightSearchListPage.flightSearchListPage.getFirstFlightArrivalTime().then($arrivalTime => {
  arrivalTime = $arrivalTime;
  arrivalTime24h = _flightSearchListPage.flightSearchListPage.convertTo24Hour(arrivalTime);
  cy.log("Arrival Time: " + arrivalTime);
});
_flightSearchListPage.flightSearchListPage.getFirstFlightDepartureCode().then(text => {
  departureCode = text.trim();
});
_flightSearchListPage.flightSearchListPage.getFirstFlightArrivalCode().then(text => {
  arrivalCode = text.trim();
});
// Select the first flight option
_flightSearchListPage.flightSearchListPage.clickExpandFlightDetails();
_flightSearchListPage.flightSearchListPage.clickSelectFirstFlight();
// Verify customer information page is loaded
_customerInformationPage.customerInformationPage.getFirstStepperName().should("have.text", "Customer information");
_customerInformationPage.customerInformationPage.verifyFirstStepperNameIsActive();
// Get price details from Customer Information Page
_customerInformationPage.customerInformationPage.getInitialPrice().then(text => {
  initialPrice = text.trim();
  cy.log("Initial Price: " + initialPrice);
});
_customerInformationPage.customerInformationPage.getBaseFare().then(text => {
  baseFare = text.trim();
  cy.log("Initial Price: " + baseFare);
});
_customerInformationPage.customerInformationPage.getTaxesAndFees().then(text => {
  taxesAndFee = text.trim();
  cy.log("Initial Price: " + taxesAndFee);
});
_customerInformationPage.customerInformationPage.getDiscountAmount().then(text => {
  discountAmount = text.trim();
  cy.log("Discount Amount: " + discountAmount);
});
_customerInformationPage.customerInformationPage.getTotalPrice().then(text => {
  totalPrice = text.trim();
  cy.log("Price After Discount: " + totalPrice);
});
// Fill in contact information using customerData
_customerInformationPage.customerInformationPage.enterContactFirstName(customerData.contactFirstName);
_customerInformationPage.customerInformationPage.enterContactLastName(customerData.contactLastName);
_customerInformationPage.customerInformationPage.enterContactEmail(customerData.contactEmail);
_customerInformationPage.customerInformationPage.selectContactCountryOfResidence(customerData.country);
_customerInformationPage.customerInformationPage.enterContactPhoneNumberCountryCode(customerData.countryCode);
_customerInformationPage.customerInformationPage.enterContactPhoneNumber(customerData.contactPhoneNumber);
// Fill in customer information using customerData
_customerInformationPage.customerInformationPage.selectFemaleGenderForPassenger();
_customerInformationPage.customerInformationPage.enterPassengerFirstAndMiddleName(customerData.passengerFirstAndMiddleName);
_customerInformationPage.customerInformationPage.enterPassengerLastName(customerData.passengerLastName);
_customerInformationPage.customerInformationPage.enterPassengerDateOfBirth(customerData.passengerDayOfBirth, customerData.passengerYearOfBirth);
_customerInformationPage.customerInformationPage.selectPassengerNationality(customerData.country);
_customerInformationPage.customerInformationPage.selectTravelProtectionNoOption();
_customerInformationPage.customerInformationPage.clickContinueToPaymentButton();
// Verify popup to confirm no travel protection
_customerInformationPage.customerInformationPage.verifyPopUpConformationToUpgradeSupportLevelIsVisible();
_customerInformationPage.customerInformationPage.clickPopUpConformationToUpgradeSupportLevelNoThanksButton();
// Verify payment page is loaded
_paymentInformationPage.paymentInformationPage.getSecondStepperName().should("have.text", "Payment information");
_paymentInformationPage.paymentInformationPage.verifySecondStepperNameIsActive();
// Verify summary details on payment page
_paymentInformationPage.paymentInformationPage.getSummaryFlightName().then(text => {
  expect(text.trim()).to.eq(flightName);
});
_paymentInformationPage.paymentInformationPage.getSummaryDepartureDate().then(text => {
  expect(text.trim()).to.eq(`Depart - ${departureDate}`);
});
_paymentInformationPage.paymentInformationPage.getSummaryDepartureTime().then(text => {
  expect(text.trim()).to.eq(departureCode + ' ' + departureTime24h);
});
_paymentInformationPage.paymentInformationPage.getSummaryArrivalTime().then(text => {
  expect(text.trim()).to.eq(arrivalCode + ' ' + arrivalTime24h);
});
_paymentInformationPage.paymentInformationPage.getInitialPrice().then(text => {
  expect(text.trim()).to.eq(initialPrice);
});
_paymentInformationPage.paymentInformationPage.getBaseFare().then(text => {
  expect(text.trim()).to.eq(baseFare);
});
_paymentInformationPage.paymentInformationPage.getTaxesAndFees().then(text => {
  expect(text.trim()).to.eq(taxesAndFee);
});
_paymentInformationPage.paymentInformationPage.getDiscountPrice().then(text => {
  expect(text.trim()).to.eq(discountAmount);
});
_paymentInformationPage.paymentInformationPage.getTotalPrice().then(text => {
  expect(text.trim()).to.eq(totalPrice);
});
// Verify Pay Now button is visible
_paymentInformationPage.paymentInformationPage.verifyPayNowButtonIsVisible();
// Verify contact details preview
_paymentInformationPage.paymentInformationPage.getContactDetailsSection().then(text => {
  expect(text.trim()).to.eq("Contact details");
});
_paymentInformationPage.paymentInformationPage.getContactNamePreview().then(text => {
  const expectedName = `${customerData.contactFirstName} ${customerData.contactLastName}`;
  expect(text.trim()).to.eq(expectedName);
});
_paymentInformationPage.paymentInformationPage.getContactEmailPreview().then(text => {
  expect(text.trim()).to.eq(customerData.contactEmail);
});
_paymentInformationPage.paymentInformationPage.getContactPhonePreview().then(text => {
  expect(text.trim()).to.eq(`+${customerData.countryCode} ${customerData.contactPhoneNumber}`);
});
_paymentInformationPage.paymentInformationPage.getPassengerDetailsSection().then(text => {
  expect(text.trim()).to.eq("Passenger details");
});
_paymentInformationPage.paymentInformationPage.getPassengerNamePreview().then(text => {
  const expectedPassengerName = `${customerData.passengerFirstAndMiddleName} ${customerData.passengerLastName}`;
  expect(text.trim()).to.eq(expectedPassengerName);
});
